    # Setting up PCA
    # accept_point is a numpy array, convert it to a tensor
    accept_point_tensor = torch.tensor(accept_point, requires_grad=False).to(
        model.device
    )
    refusal_point_tensor = torch.tensor(refusal_point, requires_grad=False).to(
        model.device
    )
